mapper.select:
SELECT 

FROM t_coupon a

WHERE a.id = #{id}

SELECT 

FROM t_coupon a




		AND a.type = #{type}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.orgname = #{orgname}


		AND a.startdate = #{startdate}


		AND a.enddate = #{enddate}


		AND a.facevalue = #{facevalue}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM t_coupon a






		ORDER BY ${page.orderBy}
